package com.zanyx.advent

import java.io.File

fun main() {
    val elfsCalories = File("input.txt").reader().useLines { lines ->
        buildList {
            var calories = 0L
            lines.forEach { line ->
                if (line.isEmpty()) {
                    add(calories)
                    calories = 0L
                } else {
                    calories += line.toLong()
                }
            }
            add(calories)
        }
    }
    println(elfsCalories.maxOrNull())
}
